(function($){$.fn.bootswitch=function(options){var opt=$.extend({object:this,positive:'.yes',negative:'.no',posval:'yes',negval:'no',input:'#showm'},options);return $(opt.object).each(function(){if(opt.negval==$(opt.input).val()){$(opt.object).children(opt.negative).addClass('active');}else if(opt.posval==$(opt.input).val()){$(opt.object).children(opt.positive).addClass('active');}$(opt.object).children(opt.positive).on('click',function(){$(opt.object).children(opt.negative).removeClass('active');$(this).addClass('active');$(opt.input).val(opt.posval).change();});$(opt.object).children(opt.negative).on('click',function(){$(opt.object).children(opt.positive).removeClass('active');$(this).addClass('active');$(opt.input).val(opt.negval).change();});});}}(jQuery));